import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AddListModal from './AddListModal';
import ConfirmationDialog from './ConfirmationDialog';
import './ShoppingListsOverview.css';

const currentUser = { id: 'user1', name: 'Owner User' };

function ShoppingListsOverview() {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showConfirm, setShowConfirm] = useState(null);
  const [filterArchived, setFilterArchived] = useState(false);

  const navigate = useNavigate();

  // Fetch shopping lists from the server
  useEffect(() => {
    const fetchLists = async () => {
      try {
        const response = await fetch('http://localhost:5000/shopping-lists');
        if (!response.ok) throw new Error('Failed to load shopping lists');
        const data = await response.json();
        setShoppingLists(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchLists();
  }, []);

  // Add a new shopping list
  const addNewList = async (listName) => {
    try {
      const response = await fetch('http://localhost:5000/shopping-lists', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: listName, owner: currentUser.id }),
      });

      if (!response.ok) throw new Error('Failed to add list');

      const newList = await response.json();
      setShoppingLists((prevLists) => [...prevLists, newList]);
      setShowModal(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  // Archive or unarchive a list
  const archiveList = async (listId, isArchived) => {
    try {
      const response = await fetch(`http://localhost:5000/shopping-lists/${listId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ isArchived }),
      });

      if (!response.ok) throw new Error('Failed to archive list');

      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list.id === listId ? { ...list, isArchived } : list
        )
      );
    } catch (err) {
      console.error(err.message);
    }
  };

  // Delete a shopping list
  const deleteList = async (listId) => {
    try {
      const response = await fetch(`http://localhost:5000/shopping-lists/${listId}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete list');

      setShoppingLists((prevLists) =>
        prevLists.filter((list) => list.id !== listId)
      );
      setShowConfirm(null);
    } catch (err) {
      console.error(err.message);
    }
  };

  // Filter lists based on "Show Archived" checkbox
  const filteredLists = shoppingLists.filter(
    (list) => (filterArchived ? true : !list.isArchived)
  );

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="overview-container">
      <header className="header">
        <h1 className="header-title">My Shopping Lists</h1>
        <button className="add-button" onClick={() => setShowModal(true)}>
          + Add List
        </button>
      </header>

      <div className="filter-section">
        <label>
          <input
            type="checkbox"
            checked={filterArchived}
            onChange={() => setFilterArchived(!filterArchived)}
          />
          Show Archived Lists
        </label>
      </div>

      <div className="tiles-container">
        {filteredLists.map((list) => (
          <div
            key={list.id}
            className={`list-tile ${list.isArchived ? 'archived' : ''}`}
            onClick={() => navigate(`/lists/${list.id}`)}
          >
            {list.owner === currentUser.id && (
              <button
                className="archive-button"
                onClick={(e) => {
                  e.stopPropagation();
                  archiveList(list.id, !list.isArchived);
                }}
                title={list.isArchived ? 'Unarchive List' : 'Archive List'}
              >
                ðŸ“¦
              </button>
            )}
            <h2>{list.name}</h2>
            {list.owner === currentUser.id && (
              <button
                className="delete-button"
                onClick={(e) => {
                  e.stopPropagation();
                  setShowConfirm(list.id);
                }}
                title="Delete List"
              >
                ðŸ—‘
              </button>
            )}
          </div>
        ))}
      </div>

      {showModal && (
        <AddListModal onAddList={addNewList} onClose={() => setShowModal(false)} />
      )}

      {showConfirm && (
        <ConfirmationDialog
          message="Are you sure you want to delete this list?"
          onConfirm={() => deleteList(showConfirm)}
          onCancel={() => setShowConfirm(null)}
        />
      )}
    </div>
  );
}

export default ShoppingListsOverview;
