import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AddListModal from './AddListModal';
import ConfirmationDialog from './ConfirmationDialog';
import './ShoppingListsOverview.css';

const BASE_URL = 'http://localhost:5000'; // Replace with your API backend URL

const currentUser = { id: 'user1', name: 'Owner User' };

function ShoppingListsOverview() {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showConfirm, setShowConfirm] = useState(null);
  const [filterArchived, setFilterArchived] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  // Fetch shopping lists from backend
  useEffect(() => {
    const fetchShoppingLists = async () => {
      try {
        const response = await fetch(`${BASE_URL}/shopping-lists`);
        const data = await response.json();
        setShoppingLists(data);
      } catch (err) {
        setError('Failed to load shopping lists.');
      } finally {
        setLoading(false);
      }
    };

    fetchShoppingLists();
  }, []);

  const addNewList = async (listName) => {
    const newList = {
      name: listName,
      isArchived: false,
      owner: currentUser.id,
    };

    try {
      const response = await fetch(`${BASE_URL}/shopping-lists`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newList),
      });

      if (!response.ok) throw new Error('Failed to add new list.');

      const addedList = await response.json();
      setShoppingLists([...shoppingLists, addedList]);
      setShowModal(false);
    } catch (err) {
      setError('Error adding new list.');
    }
  };

  const deleteList = async (listId) => {
    try {
      const response = await fetch(`${BASE_URL}/shopping-lists/${listId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete list.');

      setShoppingLists(shoppingLists.filter((list) => list.id !== listId));
      setShowConfirm(null);
    } catch (err) {
      setError('Error deleting list.');
    }
  };

  const archiveList = async (listId) => {
    try {
      const updatedLists = shoppingLists.map((list) => {
        if (list.id === listId) {
          const updatedList = { ...list, isArchived: !list.isArchived };
          fetch(`${BASE_URL}/shopping-lists/${listId}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedList),
          });
          return updatedList;
        }
        return list;
      });
      setShoppingLists(updatedLists);
    } catch (err) {
      setError('Error archiving list.');
    }
  };

  const filteredLists = shoppingLists.filter((list) => {
    return filterArchived || !list.isArchived;
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="overview-container">
      <header className="header">
        <h1 className="header-title">My Shopping Lists</h1>
        <button className="add-button" onClick={() => setShowModal(true)}>
          + Add List
        </button>
      </header>

      <div className="filter-section">
        <label>
          <input
            type="checkbox"
            checked={filterArchived}
            onChange={() => setFilterArchived(!filterArchived)}
          />
          Show Archived Lists
        </label>
      </div>

      <div className="tiles-container">
        {filteredLists.map((list) => (
          <div
            key={list.id}
            className={`list-tile ${list.isArchived ? 'archived' : ''}`}
            onClick={() => navigate(`/lists/${list.id}`)} // Correct navigation
          >
            {list.owner === currentUser.id && (
              <>
                {/* Archive Button */}
                <button
                  className="archive-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    archiveList(list.id);
                  }}
                  title={list.isArchived ? 'Unarchive List' : 'Archive List'}
                >
                  ðŸ“¦
                </button>

                {/* Delete Button */}
                <button
                  className="delete-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowConfirm(list.id);
                  }}
                  title="Delete List"
                >
                  ðŸ—‘
                </button>
              </>
            )}
            <h2>{list.name}</h2>
          </div>
        ))}
      </div>

      {/* Modals for Adding and Confirming Deletion */}
      {showModal && (
        <AddListModal onAddList={addNewList} onClose={() => setShowModal(false)} />
      )}
      {showConfirm && (
        <ConfirmationDialog
          message="Are you sure you want to delete this list?"
          onConfirm={() => deleteList(showConfirm)}
          onCancel={() => setShowConfirm(null)}
        />
      )}
    </div>
  );
}

export default ShoppingListsOverview;
