import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AddListModal from './AddListModal';
import ConfirmationDialog from './ConfirmationDialog';
import './ShoppingListsOverview.css';

const API_BASE_URL = 'http://localhost:5000/api'; // Backend server URL

const currentUser = { id: 'user1', name: 'Owner User' }; // Mock current user data

function ShoppingListsOverview() {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showConfirm, setShowConfirm] = useState(null);
  const [filterArchived, setFilterArchived] = useState(false);

  const navigate = useNavigate();

  // Fetch shopping lists from the server
  const fetchShoppingLists = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/shopping-lists`);
      if (!response.ok) throw new Error('Failed to fetch shopping lists');

      const data = await response.json();
      setShoppingLists(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Add new list (POST request)
  const addNewList = async (listName) => {
    const newList = {
      name: listName,
      isArchived: false,
      owner: currentUser.id,
    };

    try {
      const response = await fetch(`${API_BASE_URL}/shopping-lists`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newList),
      });

      if (!response.ok) throw new Error('Failed to add shopping list');

      const createdList = await response.json();
      setShoppingLists([...shoppingLists, createdList]);
      setShowModal(false);
    } catch (err) {
      setError(err.message);
    }
  };

  // Delete a list (DELETE request)
  const deleteList = async (listId) => {
    try {
      const response = await fetch(`${API_BASE_URL}/shopping-lists/${listId}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete shopping list');

      setShoppingLists(shoppingLists.filter((list) => list.id !== listId));
      setShowConfirm(null);
    } catch (err) {
      setError(err.message);
    }
  };

  // Archive/Unarchive list (PATCH request)
  const archiveList = async (listId, isArchived) => {
    try {
      const response = await fetch(`${API_BASE_URL}/shopping-lists/${listId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ isArchived: !isArchived }),
      });

      if (!response.ok) throw new Error('Failed to archive/unarchive shopping list');

      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list.id === listId ? { ...list, isArchived: !isArchived } : list
        )
      );
    } catch (err) {
      setError(err.message);
    }
  };

  // Filter archived/non-archived lists
  const filteredLists = shoppingLists.filter(
    (list) => (filterArchived ? true : !list.isArchived)
  );

  useEffect(() => {
    fetchShoppingLists();
  }, []);

  return (
    <div className="overview-container">
      <header className="header">
        <h1 className="header-title">My Shopping Lists</h1>
        <button className="add-button" onClick={() => setShowModal(true)}>
          + Add List
        </button>
      </header>

      <div className="filter-section">
        <label>
          <input
            type="checkbox"
            checked={filterArchived}
            onChange={() => setFilterArchived(!filterArchived)}
          />
          Show Archived Lists
        </label>
      </div>

      {/* Loading and Error States */}
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      {/* Shopping Lists */}
      <div className="tiles-container">
        {filteredLists.map((list) => (
          <div
            key={list.id}
            className={`list-tile ${list.isArchived ? 'archived' : ''}`}
            onClick={() => navigate(`/lists/${list.id}`)}
          >
            {list.owner === currentUser.id && (
              <button
                className="archive-button"
                onClick={(e) => {
                  e.stopPropagation();
                  archiveList(list.id, list.isArchived);
                }}
                title={list.isArchived ? 'Unarchive List' : 'Archive List'}
              >
                ðŸ“¦
              </button>
            )}
            <h2>{list.name}</h2>
            {list.owner === currentUser.id && (
              <button
                className="delete-button"
                onClick={(e) => {
                  e.stopPropagation();
                  setShowConfirm(list.id);
                }}
                title="Delete List"
              >
                ðŸ—‘
              </button>
            )}
          </div>
        ))}
      </div>

      {/* Modals */}
      {showModal && <AddListModal onAddList={addNewList} onClose={() => setShowModal(false)} />}
      {showConfirm && (
        <ConfirmationDialog
          message="Are you sure you want to delete this list?"
          onConfirm={() => deleteList(showConfirm)}
          onCancel={() => setShowConfirm(null)}
        />
      )}
    </div>
  );
}

export default ShoppingListsOverview;
